src/components/SafeBackgroundImage.tsx.content:
"use client";

import { useState, useEffect } from "react";

type SafeBackgroundImageProps = {
  src: string;
  className?: string;
  children?: React.ReactNode;
};

const placeholderGradient = "linear-gradient(135deg, #fef3c7, #ffedd5)";

export function SafeBackgroundImage({ src, className, children }: SafeBackgroundImageProps) {
  const [bgImage, setBgImage] = useState(`url(${src})`);
  const [hasError, setHasError] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (!src || hasError) {
      setIsLoading(false);
      return;
    }

    setIsLoading(true);
    const img = new Image();
    img.onload = () => {
      setIsLoading(false);
      setBgImage(`url(${src})`);
    };
    img.onerror = () => {
      setIsLoading(false);
      setHasError(true);
      setBgImage(placeholderGradient);
    };
    img.src = src;
  }, [src, hasError]);

  return (
    <div
      className={`${className} ${isLoading ? "blur-sm" : "blur-0"} transition-all duration-300`}
      style={{ backgroundImage: bgImage, backgroundSize: "cover", backgroundPosition: "center" }}
    >
      {children}
    </div>
  );
}

src/components/SafeImage.tsx.content:
"use client";

import { useState } from "react";

type SafeImageProps = {
  src: string;
  alt: string;
  className?: string;
  fallbackText?: string;
};

const placeholderSvg = (text: string) =>
  `data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='64' height='64'><rect width='100%' height='100%' fill='%23e2e8f0'/><text x='50%' y='50%' dominant-baseline='middle' text-anchor='middle' fill='%2394a3b8' font-size='18'>${encodeURIComponent(text)}</text></svg>`;

export function SafeImage({ src, alt, className, fallbackText = "?" }: SafeImageProps) {
  const [imgSrc, setImgSrc] = useState(src);
  const [hasError, setHasError] = useState(false);

  const handleError = () => {
    if (!hasError) {
      setHasError(true);
      setImgSrc(placeholderSvg(fallbackText));
    }
  };

  return (
    <img
      src={imgSrc}
      alt={alt}
      className={className}
      onError={handleError}
    />
  );
}

