import { SafeBackgroundImage } from "@/components/SafeBackgroundImage";
import { TechStackTabs } from "@/components/TechStackTabs";
import { AIAgentBuilder } from "@/components/AIAgentBuilder";
import { ServicesSection } from "@/components/ServicesSection";
import { AboutSection } from "@/components/AboutSection";
import { TeamCarousel } from "@/components/TeamCarousel";
import { normalizeImageUrl } from "@/lib/images";
import { apiFetch } from "@/lib/api";

type HomeResponse = {
    slides: {
        id: number;
        heading: string;
        subHeading: string;
        tags: string[];
        backgroundImageUrl: string;
        isActive: boolean;
        displayOrder: number;
    }[];
    about: {
        mission: string;
        vision: string;
        teamIntro: string;
        expertise: string[];
        coreValues: string[];
    } | null;
    team: {
        id: number;
        name: string;
        role: string;
        bio: string;
        expertise: string[];
        avatarUrl: string;
        isActive: boolean;
        displayOrder: number;
    }[];
    posts: {
        id: number;
        title: string;
        excerpt: string;
        slug: string;
        imageUrl: string;
        publishedAt: string | null;
    }[];
};

export default function HomePage() {
    const [data, setData] = useState<HomeResponse | null>(null);

    useEffect(() => {
        async function fetchData() {
            try {
                const res = await apiFetch("/api/home");
                if (res.ok) {
                    setData(await res.json());
                } else {
                    console.error("Failed to fetch home data:", res.status, res.statusText);
                }
            } catch (error) {
                console.error("Error fetching home data:", error);
            }
        }
        fetchData();
    }, []);

    const slides = data?.slides ?? [];
    const about = data?.about ?? null;
    const team = data?.team ?? [];
    const posts = data?.posts ?? [];

    const comparisonData = [
