src/app/login/LoginPage.tsx.content:
import { useState, Suspense } from "react";
import { useNavigate, Link } from "react-router-dom";
import { apiFetch } from "@/lib/api";

function LoginForm() {
    const navigate = useNavigate();
    const [error, setError] = useState<string | null>(null);
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        setError(null);
        setLoading(true);

        const formData = new FormData(e.currentTarget);
        const payload = {
            email: formData.get("email") as string,
            password: formData.get("password") as string,
        };

        // Try admin login first
        const adminRes = await apiFetch("/api/admin/login", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(payload),
            credentials: "include",
        });

        if (adminRes.ok) {
            try {
                const data = await adminRes.json().catch(() => null);
                if (data?.token) {
                    localStorage.setItem("nano_admin_token", data.token);
                }
            } catch { }
            setLoading(false);
            // Force reload for admin to ensure clean state or navigating to a potentially different app structure
            window.location.href = "/admin";
            return;
        }

        // If admin login fails, try user login
        const userRes = await apiFetch("/api/auth/login", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(payload),
            credentials: "include",
        });

        setLoading(false);

        if (userRes.ok) {
            navigate("/");
            return;
        }

        setError("Invalid credentials. Please try again.");
    };

    return (
        <div className="min-h-screen flex">
            <div className="hidden lg:flex lg:w-1/2 relative">
                <img
                    src="https://images.unsplash.com/photo-1677442136019-21780ecad995?w=1200&h=1600&fit=crop"
                    alt="AI Technology"
                    className="absolute inset-0 w-full h-full object-cover"
                />
                <div className="absolute inset-0 bg-gradient-to-br from-slate-900/90 via-slate-900/80 to-slate-900/70"></div>
                <div className="relative z-10 flex flex-col justify-between p-12">
                    <div></div>
                    <div className="max-w-md">
                        <h2 className="text-3xl font-bold text-white mb-4">Welcome Back</h2>
                        <p className="text-gray-300 leading-relaxed">
                            Securely access your dashboard. Admins are automatically redirected to the console.
                        </p>
                    </div>
                    <div className="flex items-center gap-6 text-sm text-gray-400">
                        <span className="flex items-center gap-2">
                            <svg className="w-5 h-5 text-green-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m5.618-4.016A11.955 11.955 0 0112 2.944a11.955 11.955 0 01-8.618 3.04A12.02 12.02 0 003 9c0 5.591 3.824 10.29 9 11.622 5.176-1.332 9-6.03 9-11.622 0-1.042-.133-2.052-.382-3.016z" />
                            </svg>
                            Secure Login
                        </span>
                        <span className="flex items-center gap-2">
                            <svg className="w-5 h-5 text-green-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
                            </svg>
                            256-bit Encryption
                        </span>
                    </div>
                </div>
            </div>

            <div className="flex-1 flex flex-col justify-center bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 px-6 lg:px-12">
                <div className="mx-auto w-full max-w-md">

                    <div className="mb-8">
                        <div className="inline-flex items-center gap-2 px-3 py-1.5 rounded-full bg-orange-500/10 border border-orange-500/20 text-orange-400 text-xs font-medium mb-4">
                            Secure Login
                        </div>
                        <h1 className="text-3xl font-bold text-white">Welcome Back</h1>
                        <p className="mt-3 text-gray-400">Securely access your dashboard. Admins are automatically redirected to the console.</p>
                    </div>

                    <form onSubmit={handleSubmit} className="space-y-5">
                        <div>
                            <label className="block text-sm font-medium text-gray-300 mb-2">Email Address</label>
                            <input
                                name="email"
                                type="email"
                                placeholder="you@example.com"
                                className="w-full rounded-xl border border-white/10 bg-white/5 px-4 py-3.5 text-white placeholder-gray-500 outline-none focus:border-orange-500 focus:ring-2 focus:ring-orange-500/20 transition"
                                required
                            />
                        </div>

                        <div>
                            <label className="block text-sm font-medium text-gray-300 mb-2">Password</label>
                            <input
                                name="password"
                                type="password"
                                placeholder="Enter your password"
                                className="w-full rounded-xl border border-white/10 bg-white/5 px-4 py-3.5 text-white placeholder-gray-500 outline-none focus:border-orange-500 focus:ring-2 focus:ring-orange-500/20 transition"
                                required
                            />
                        </div>

                        {error && (
                            <div className="flex items-center gap-2 p-3 rounded-lg bg-red-500/10 border border-red-500/20">
                                <svg className="w-5 h-5 text-red-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                                </svg>
                                <p className="text-sm text-red-400">{error}</p>
                            </div>
                        )}

                        <button
                            type="submit"
                            disabled={loading}
                            className="w-full rounded-xl bg-gradient-to-r from-orange-500 to-amber-500 px-4 py-4 text-base font-semibold text-white shadow-lg shadow-orange-500/25 transition hover:-translate-y-0.5 hover:shadow-orange-500/40 disabled:opacity-50 disabled:cursor-not-allowed"
                        >
                            {loading ? (
                                <span className="flex items-center justify-center gap-2">
                                    <svg className="animate-spin h-5 w-5" viewBox="0 0 24 24">
                                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" />
                                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z" />
                                    </svg>
                                    Signing in...
                                </span>
                            ) : (
                                "Sign In"
                            )}
                        </button>
                    </form>

                    <div className="mt-8 text-center space-y-4">
                        <p className="text-gray-400">
                            Don't have an account?{" "}
                            <Link to="/signup" className="font-semibold text-orange-400 hover:text-orange-300 transition">
                                Sign up
                            </Link>
                        </p>
                        <Link
                            to="/"
                            className="inline-flex items-center justify-center gap-2 rounded-xl bg-white px-6 py-3 text-sm font-semibold text-gray-900 shadow-lg transition hover:-translate-y-0.5 hover:shadow-xl"
                        >
                            ← Back to website
                        </Link>
                    </div>

                    <p className="mt-8 text-center text-sm text-gray-500">
                        By signing in, you agree to our{" "}
                        <Link to="/terms" className="text-orange-400 hover:underline">Terms of Service</Link>
                        {" "}and{" "}
                        <Link to="/privacy-policy" className="text-orange-400 hover:underline">Privacy Policy</Link>
                    </p>
                </div>
            </div>
        </div>
    );
}

export default function LoginPage() {
    return (
        <Suspense fallback={
            <div className="min-h-screen bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 flex items-center justify-center">
                <div className="animate-spin h-8 w-8 border-4 border-orange-500 border-t-transparent rounded-full"></div>
            </div>
        }>
            <LoginForm />
        </Suspense>
    );
}

src/app/globals.css.content:
@import "tailwindcss";

:root {
  --primary: #4F46E5;
  --secondary: #22C55E;
  --accent: #F59E0B;
  --neutral-900: #1F2937;
  --neutral-700: #374151;
  --neutral-500: #6B7280;
  --neutral-300: #D1D5DB;
  --neutral-100: #F3F4F6;
  --surface-hero: #FFFFFF;
  --surface-panel: #F9FAFB;
  --background: #FFFFFF;
  --foreground: #111827;
}

@theme inline {
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --font-sans: 'Inter', system-ui, -apple-system, sans-serif;
  --font-mono: 'Ui-Monospace', 'SFMono-Regular', 'Menlo', 'Monaco', 'Consolas', "Liberation Mono", "Courier New", monospace;
}

html[data-scroll-behavior="smooth"] {
  scroll-behavior: smooth;
}

body {
  background: var(--background);
  color: var(--foreground);
  font-family: var(--font-sans);
  min-height: 100vh;
}

a {
  color: inherit;
  text-decoration: none;
}

* {
  box-sizing: border-box;
}

.line-clamp-2 {
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

.line-clamp-3 {
  display: -webkit-box;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: rgba(0, 0, 0, 0.05);
}

::-webkit-scrollbar-thumb {
  background: rgba(0, 0, 0, 0.1);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: rgba(0, 0, 0, 0.2);
}

::selection {
  background: rgba(79, 70, 229, 0.2);
  color: #1F2937;
}

@keyframes scroll-left {
  0% { transform: translateX(0); }
  100% { transform: translateX(-50%); }
}

@keyframes scroll-right {
  0% { transform: translateX(-50%); }
  100% { transform: translateX(0); }
}

.animate-scroll-left {
  animation: scroll-left 20s linear infinite;
}

.animate-scroll-right {
  animation: scroll-right 20s linear infinite;
}

.animate-scroll-left-slow {
  animation: scroll-left 25s linear infinite;
}

.scrollbar-hide {
  -ms-overflow-style: none;
  scrollbar-width: none;
}

.scrollbar-hide::-webkit-scrollbar {
  display: none;
}

html {
  -webkit-text-size-adjust: 100%;
  text-size-adjust: 100%;
}

.backdrop-blur-sm {
  -webkit-backdrop-filter: blur(4px);
  backdrop-filter: blur(4px);
}

.backdrop-blur {
  -webkit-backdrop-filter: blur(8px);
  backdrop-filter: blur(8px);
}

.backdrop-blur-md {
  -webkit-backdrop-filter: blur(12px);
  backdrop-filter: blur(12px);
}

.backdrop-blur-lg {
  -webkit-backdrop-filter: blur(16px);
  backdrop-filter: blur(16px);
}

.select-none {
  -webkit-user-select: none;
  user-select: none;
}

.select-text {
  -webkit-user-select: text;
  user-select: text;
}

.select-all {
  -webkit-user-select: all;
  user-select: all;
}

.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border-width: 0;
}

src/components/TeamCarousel.tsx.content:
"use client";

import { SafeImage } from "@/components/SafeImage";
import { normalizeImageUrl } from "@/lib/images";

type TeamMember = {
  id: number;
  name: string;
  role: string;
  bio: string;
  expertise: string[];
  avatarUrl: string;
  isActive: boolean;
  displayOrder: number;
};

export function TeamCarousel({ team }: { team: TeamMember[] }) {
  if (team.length === 0) {
    return (
      <div className="col-span-full rounded-2xl border border-dashed border-gray-300 bg-gray-50 p-8 text-center text-gray-500">
        Add team members in the admin panel to showcase your team.
      </div>
    );
  }

  const duplicatedTeam = [...team, ...team];

  return (
    <div className="relative overflow-hidden">
      <div className="absolute left-0 top-0 bottom-0 w-16 md:w-24 bg-gradient-to-r from-white to-transparent z-10 pointer-events-none"></div>
      <div className="absolute right-0 top-0 bottom-0 w-16 md:w-24 bg-gradient-to-l from-white to-transparent z-10 pointer-events-none"></div>

      <div
        className="flex gap-6 animate-[scroll-left_75s_linear_infinite] hover:[animation-play-state:paused]"
        style={{
          width: `${duplicatedTeam.length * 344}px`,
        }}
      >
        {duplicatedTeam.map((member, index) => (
          <div
            key={`${member.id}-${index}`}
            className="flex-shrink-0 w-[320px] rounded-2xl border border-gray-100 bg-white p-6 transition-transform hover:-translate-y-1 hover:border-orange-200 hover:shadow-xl"
          >
            <div className="flex items-center gap-4">
              <SafeImage
                src={normalizeImageUrl(member.avatarUrl)}
                alt={member.name}
                className="h-16 w-16 rounded-full object-cover ring-2 ring-orange-200"
                fallbackText={member.name.charAt(0).toUpperCase()}
              />
              <div>
                <div className="text-lg font-semibold text-gray-900">{member.name}</div>
                <div className="text-sm text-orange-600">{member.role}</div>
              </div>
            </div>
            <p className="mt-4 text-sm text-gray-600 line-clamp-3">{member.bio}</p>
            <div className="mt-4 flex flex-wrap gap-2">
              {member.expertise.map((tag) => (
                <span
                  key={tag}
                  className="rounded-full bg-orange-50 px-2.5 py-1 text-xs text-orange-700"
                >
                  {tag}
                </span>
              ))}
            </div>
          </div>
        ))}
      </div>


    </div>
  );
}

src/components/IndustriesDropdown.tsx.content:
"use client";

import { Link } from "react-router-dom";
import { motion } from "framer-motion";

import { industries } from "../lib/data/industries";

const industriesData = industries;

interface IndustriesDropdownProps {
  onClose?: () => void;
}

export function IndustriesDropdown({ onClose }: IndustriesDropdownProps) {
  return (
    <motion.div
      initial={{ opacity: 0, y: -10 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -10 }}
      transition={{ duration: 0.2 }}
      className="absolute left-1/2 -translate-x-1/2 top-full mt-3 w-[75vw] max-w-6xl rounded-2xl border border-gray-100 bg-white shadow-2xl backdrop-blur-xl"
      onClick={(e) => e.stopPropagation()}
    >
      <div className="grid grid-cols-7 gap-0 divide-x divide-gray-200 p-4">
        {industriesData.map((industry, index) => (
          <div key={industry.id} className={`flex flex-col ${index !== 0 ? 'pl-4' : ''}`}>
            {/* Header Section */}
            <Link
              to={`/industries/${industry.id}`}
              className="group flex flex-col mb-4 pb-4 border-b border-gray-100 transition-all hover:bg-orange-50 hover:text-orange-600 px-2 py-2 rounded-lg"
              onClick={onClose}
            >
              <div className="flex items-start gap-2 mb-2">
                <motion.span
                  className="text-3xl leading-none"
                  whileHover={{ y: -4 }}
                  transition={{ duration: 0.3 }}
                >
                  {industry.icon}
                </motion.span>
              </div>
              <h3 className="text-sm font-bold text-gray-900 uppercase tracking-widest group-hover:text-orange-600 transition leading-tight">
                {industry.name}
              </h3>
            </Link>

            {/* Sub-items Section */}
            <div className="flex flex-col gap-1 items-center">
              {industry.subIndustries.map((sub, subIndex) => (
                <motion.div
                  key={sub.id}
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 0.05 + subIndex * 0.02 }}
                >
                  <Link
                    to={`/industries/${industry.id}/${sub.id}`}
                    className="group flex items-center justify-center gap-2 text-xs font-medium text-gray-700 hover:text-orange-600 hover:bg-orange-50 transition-all py-2 px-2.5 rounded-md"
                    onClick={onClose}
                  >
                    <motion.span
                      className="text-base flex-shrink-0"
                      whileHover={{ scale: 1.2 }}
                      transition={{ duration: 0.3 }}
                    >
                      {sub.icon}
                    </motion.span>
                    <span className="group-hover:translate-x-0.5 transition-transform">{sub.name}</span>
                  </Link>
                </motion.div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </motion.div>
  );
}
