      "max-snippet": -1,
    },
  },
  verification: {
    google: "your-google-verification-code",
  },
  alternates: {
    canonical: siteUrl,
  },
};

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  return (
    <html lang="en" suppressHydrationWarning>
      <head>
        <link rel="icon" href="/nanoflows-logo.png" type="image/png" />
        <link rel="apple-touch-icon" href="/nanoflows-logo.png" />
        <meta name="theme-color" content="#1e293b" />
      </head>
      <body
        suppressHydrationWarning
        className={`${geistSans.variable} ${geistMono.variable} antialiased`}
      >
        {children}
        <FloatingButtons />
      </body>
    </html>
  );
}

src/app/auth/page.tsx.content:
"use client";

import { useRouter, useSearchParams } from "next/navigation";
import { useMemo, useState, Suspense } from "react";
import Image from "next/image";
import { apiFetch } from "@/lib/api";

type Mode = "login" | "signup";

function AuthForm() {
  const router = useRouter();
  const search = useSearchParams();
  const initialMode = (search.get("mode") as Mode) || "login";
  const [mode, setMode] = useState<Mode>(initialMode);
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);

  const title = useMemo(
    () => (mode === "login" ? "Welcome Back" : "Create Your Account"),
    [mode],
  );

  const subtitle = useMemo(
    () =>
      mode === "login"
        ? "Sign in to access your NanoFlows dashboard and admin panel."
        : "Join NanoFlows to explore AI-driven experiences.",
    [mode],
  );

  const handleLogin = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setError(null);
    setLoading(true);
    const formData = new FormData(e.currentTarget);
    const payload = {
      email: formData.get("email") as string,
      password: formData.get("password") as string,
    };
    const res = await apiFetch("/api/auth/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });
    setLoading(false);
    if (!res.ok) {
      setError("Invalid credentials");
      return;
    }
    const data = (await res.json().catch(() => null)) as { role?: string } | null;
    if (data?.role === "admin") {
      router.push("/admin");
    } else {
      router.push("/");
    }
  };

  const handleSignup = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setError(null);
    setLoading(true);
    const formData = new FormData(e.currentTarget);
    const payload = {
      name: formData.get("name") as string,
      email: formData.get("email") as string,
      password: formData.get("password") as string,
    };
    const res = await apiFetch("/api/auth/signup", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });
    setLoading(false);
    if (!res.ok) {
      const data = await res.json().catch(() => null);
      setError(data?.error ?? "Could not sign up");
      return;
    }
    router.push("/");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900">
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute -left-1/4 top-0 h-[600px] w-[600px] rounded-full bg-orange-500/10 blur-[120px]" />
        <div className="absolute -right-1/4 bottom-0 h-[600px] w-[600px] rounded-full bg-amber-500/10 blur-[120px]" />
        <div className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 h-[400px] w-[400px] rounded-full bg-orange-600/5 blur-[100px]" />
      </div>

      <div className="relative mx-auto flex min-h-screen max-w-[1200px] flex-col gap-10 px-6 py-16 lg:flex-row lg:items-center lg:justify-between">
        <div className="space-y-6 lg:w-1/2">
          <a href="/" className="inline-flex items-center gap-3 group">
            <div className="flex h-12 w-12 items-center justify-center rounded-xl bg-gradient-to-br from-orange-500 to-amber-500 shadow-lg shadow-orange-500/30 group-hover:shadow-orange-500/50 transition">
              <span className="text-2xl font-black text-white">N</span>
            </div>
            <div>
              <div className="text-xl font-bold tracking-tight text-white">NanoFlows</div>
              <div className="text-[10px] uppercase tracking-[0.25em] text-orange-400">
                AI Software Technologies
              </div>
            </div>
          </a>

          <h1 className="text-4xl font-extrabold leading-tight text-white md:text-[52px]">{title}</h1>
          <p className="text-lg text-gray-400">{subtitle}</p>

          <div className="flex items-center gap-3">
            <button
              onClick={() => setMode("login")}
              className={`rounded-xl px-6 py-3 text-sm font-semibold transition ${
                mode === "login"
                  ? "bg-gradient-to-r from-orange-500 to-amber-500 text-white shadow-lg shadow-orange-500/25"
                  : "border border-white/20 bg-white/5 text-gray-300 hover:border-orange-500/50 hover:bg-white/10"
              }`}
            >
              Login
            </button>
            <button
              onClick={() => setMode("signup")}
              className={`rounded-xl px-6 py-3 text-sm font-semibold transition ${
                mode === "signup"
                  ? "bg-gradient-to-r from-orange-500 to-amber-500 text-white shadow-lg shadow-orange-500/25"
                  : "border border-white/20 bg-white/5 text-gray-300 hover:border-orange-500/50 hover:bg-white/10"
              }`}
            >
              Sign Up
            </button>
          </div>

          <div className="mt-8 hidden lg:block">
            <div className="flex items-center gap-4 text-gray-400">
              <div className="flex -space-x-2">
                <div className="h-10 w-10 rounded-full bg-gradient-to-br from-orange-400 to-amber-500 ring-2 ring-slate-800 flex items-center justify-center text-white text-sm font-semibold">AI</div>
                <div className="h-10 w-10 rounded-full bg-gradient-to-br from-amber-400 to-orange-500 ring-2 ring-slate-800 flex items-center justify-center text-white text-sm font-semibold">ML</div>
                <div className="h-10 w-10 rounded-full bg-gradient-to-br from-orange-500 to-red-500 ring-2 ring-slate-800 flex items-center justify-center text-white text-sm font-semibold">+</div>
              </div>
              <span className="text-sm">Trusted by 500+ businesses worldwide</span>
            </div>
          </div>
        </div>

        <div className="w-full max-w-md rounded-2xl border border-white/10 bg-white/5 backdrop-blur-xl p-8 shadow-2xl">
          {mode === "login" ? (
            <form onSubmit={handleLogin} className="space-y-5">
              <div className="space-y-2">
                <label className="text-sm font-medium text-gray-300">Email</label>
                <input
                  name="email"
                  type="email"
                  placeholder="you@company.com"
                  className="w-full rounded-xl border border-white/10 bg-white/5 px-4 py-3.5 text-white placeholder-gray-500 outline-none transition focus:border-orange-500 focus:ring-2 focus:ring-orange-500/20"
                  required
                />
              </div>
              <div className="space-y-2">
                <label className="text-sm font-medium text-gray-300">Password</label>
                <input
                  name="password"
                  type="password"
                  placeholder="Enter your password"
                  className="w-full rounded-xl border border-white/10 bg-white/5 px-4 py-3.5 text-white placeholder-gray-500 outline-none transition focus:border-orange-500 focus:ring-2 focus:ring-orange-500/20"
                  required
                />
              </div>
              {error && (
                <p className="rounded-lg bg-red-500/10 border border-red-500/20 px-4 py-2 text-sm text-red-400">{error}</p>
              )}
              <button
                type="submit"
                disabled={loading}
                className="w-full rounded-xl bg-gradient-to-r from-orange-500 to-amber-500 px-4 py-4 text-sm font-semibold text-white shadow-lg shadow-orange-500/25 transition hover:-translate-y-0.5 hover:shadow-orange-500/40 disabled:opacity-70"
              >
                {loading ? "Signing in..." : "Sign In"}
              </button>
              <div className="text-center">
                <p className="text-sm text-gray-500">
                  Don't have an account?{" "}
                  <button type="button" onClick={() => setMode("signup")} className="text-orange-400 hover:text-orange-300 font-medium">
                    Sign up
                  </button>
                </p>
              </div>
            </form>
          ) : (
            <form onSubmit={handleSignup} className="space-y-5">
              <div className="space-y-2">
                <label className="text-sm font-medium text-gray-300">Full Name</label>
                <input
                  name="name"
                  type="text"
                  placeholder="John Doe"
                  className="w-full rounded-xl border border-white/10 bg-white/5 px-4 py-3.5 text-white placeholder-gray-500 outline-none transition focus:border-orange-500 focus:ring-2 focus:ring-orange-500/20"
                  required
                />
              </div>
              <div className="space-y-2">
                <label className="text-sm font-medium text-gray-300">Email</label>
                <input
                  name="email"
                  type="email"
                  placeholder="you@company.com"
                  className="w-full rounded-xl border border-white/10 bg-white/5 px-4 py-3.5 text-white placeholder-gray-500 outline-none transition focus:border-orange-500 focus:ring-2 focus:ring-orange-500/20"
                  required
                />
              </div>
              <div className="space-y-2">
                <label className="text-sm font-medium text-gray-300">Password</label>
                <input
                  name="password"
                  type="password"
                  placeholder="Create a strong password"
                  className="w-full rounded-xl border border-white/10 bg-white/5 px-4 py-3.5 text-white placeholder-gray-500 outline-none transition focus:border-orange-500 focus:ring-2 focus:ring-orange-500/20"
                  required
                />
              </div>
              {error && (
                <p className="rounded-lg bg-red-500/10 border border-red-500/20 px-4 py-2 text-sm text-red-400">{error}</p>
              )}
              <button
                type="submit"
                disabled={loading}
                className="w-full rounded-xl bg-gradient-to-r from-orange-500 to-amber-500 px-4 py-4 text-sm font-semibold text-white shadow-lg shadow-orange-500/25 transition hover:-translate-y-0.5 hover:shadow-orange-500/40 disabled:opacity-70"
              >
                {loading ? "Creating account..." : "Create Account"}
              </button>
              <div className="text-center">
                <p className="text-sm text-gray-500">
                  Already have an account?{" "}
                  <button type="button" onClick={() => setMode("login")} className="text-orange-400 hover:text-orange-300 font-medium">
                    Sign in
                  </button>
                </p>
              </div>
            </form>
          )}
        </div>
      </div>
    </div>
  );
}

export default function AuthPage() {
  return (
    <Suspense fallback={
      <div className="min-h-screen bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 flex items-center justify-center">
        <div className="animate-spin h-8 w-8 border-4 border-orange-500 border-t-transparent rounded-full"></div>
      </div>
    }>
      <AuthForm />
    </Suspense>
  );
}

src/components/TeamCarousel.tsx.content:
"use client";

import { SafeImage } from "@/components/SafeImage";
import { normalizeImageUrl } from "@/lib/images";

type TeamMember = {
  id: number;
  name: string;
  role: string;
  bio: string;
  expertise: string[];
  avatarUrl: string;
  isActive: boolean;
  displayOrder: number;
};

export function TeamCarousel({ team }: { team: TeamMember[] }) {
  if (team.length === 0) {
    return (
      <div className="col-span-full rounded-2xl border border-dashed border-gray-300 bg-gray-50 p-8 text-center text-gray-500">
        Add team members in the admin panel to showcase your team.
      </div>
    );
  }

  const duplicatedTeam = [...team, ...team];

  return (
    <div className="relative overflow-hidden">
      <div className="absolute left-0 top-0 bottom-0 w-16 md:w-24 bg-gradient-to-r from-white to-transparent z-10 pointer-events-none"></div>
      <div className="absolute right-0 top-0 bottom-0 w-16 md:w-24 bg-gradient-to-l from-white to-transparent z-10 pointer-events-none"></div>

      <div
        className="flex gap-6 animate-scroll hover:pause-animation"
        style={{
          width: `${duplicatedTeam.length * 344}px`,
        }}
      >
        {duplicatedTeam.map((member, index) => (
          <div
            key={`${member.id}-${index}`}
            className="flex-shrink-0 w-[320px] rounded-2xl border border-gray-100 bg-white p-6 transition-transform hover:-translate-y-1 hover:border-orange-200 hover:shadow-xl"
          >
            <div className="flex items-center gap-4">
              <SafeImage
                src={normalizeImageUrl(member.avatarUrl)}
                alt={member.name}
                className="h-16 w-16 rounded-full object-cover ring-2 ring-orange-200"
                fallbackText={member.name.charAt(0).toUpperCase()}
              />
              <div>
                <div className="text-lg font-semibold text-gray-900">{member.name}</div>
                <div className="text-sm text-orange-600">{member.role}</div>
              </div>
            </div>
            <p className="mt-4 text-sm text-gray-600 line-clamp-3">{member.bio}</p>
            <div className="mt-4 flex flex-wrap gap-2">
              {member.expertise.map((tag) => (
                <span
                  key={tag}
                  className="rounded-full bg-orange-50 px-2.5 py-1 text-xs text-orange-700"
                >
                  {tag}
                </span>
              ))}
            </div>
          </div>
        ))}
      </div>

      <style jsx>{`
        @keyframes scroll {
          0% {
            transform: translateX(0);
          }
          100% {
            transform: translateX(-50%);
          }
        }
        .animate-scroll {
          animation: scroll 30s linear infinite;
        }
        .animate-scroll:hover {
          animation-play-state: paused;
        }
      `}</style>
    </div>
  );
}

src/components/ServiceCategorySidebar.ts.content:
"use client";

import { useState, useEffect, useCallback } from "react";
import { motion, AnimatePresence } from "framer-motion";
import Link from "next/link";
import Image from "next/image";
import type { StaticImageData } from "next/image";

type Service = {
  id: string;
  title: string;
  image: string | StaticImageData;
  category: string;
};

type ServiceCategorySidebarProps = {
  currentServiceId: string;
  currentCategory: string;
  services: Service[];
  hideWhenPastHero?: boolean;
};

export function ServiceCategorySidebar({
  currentServiceId,
  currentCategory,
  services,
  hideWhenPastHero = true,
}: ServiceCategorySidebarProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [isVisible, setIsVisible] = useState(true);

  const checkVisibility = useCallback(() => {
    const aboutSection = document.getElementById("about-service-section");
    if (!aboutSection) {
      setIsVisible(true);
      return;
    }

    const rect = aboutSection.getBoundingClientRect();
    const threshold = 200;
    const shouldHide = rect.top < threshold;
    setIsVisible(!shouldHide);
  }, []);

  useEffect(() => {
    if (!hideWhenPastHero) return;

    let ticking = false;
    const handleScroll = () => {
      if (!ticking) {
        window.requestAnimationFrame(() => {
          checkVisibility();
          ticking = false;
        });
        ticking = true;
      }
    };

    window.addEventListener("scroll", handleScroll, { passive: true });
    checkVisibility();

    return () => window.removeEventListener("scroll", handleScroll);
  }, [hideWhenPastHero, checkVisibility]);

  const categoryServices = services.filter(
    (s) => s.category === currentCategory && s.id !== currentServiceId
  );

  if (categoryServices.length === 0) return null;

  return (
    <>
      <AnimatePresence>
        {isVisible && (
          <motion.div
            initial={{ x: -100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            exit={{ x: -100, opacity: 0 }}
            transition={{ type: "spring", damping: 25, stiffness: 200 }}
            className="fixed left-0 top-1/2 -translate-y-1/2 z-50"
          >
            <motion.button
              onClick={() => setIsOpen(true)}
              className="group flex items-center bg-gradient-to-r from-orange-500 to-orange-600 text-white rounded-r-xl shadow-lg hover:shadow-xl transition-all duration-300"
              whileHover={{ x: 5 }}
              style={{ originX: 0 }}
              aria-label="Open related services sidebar"
            >
              <div className="flex items-center gap-1 sm:gap-2 py-3 sm:py-4 px-2 sm:px-3">
                <motion.span
                  className="text-[10px] sm:text-xs font-semibold uppercase tracking-wider whitespace-nowrap"
                  style={{ writingMode: "vertical-rl", textOrientation: "mixed" }}
                  animate={{
                    opacity: [1, 0.5, 1],
                  }}
                  transition={{
                    duration: 2,
                    repeat: Infinity,
                    ease: "easeInOut",
                  }}
                >
                  More Services
                </motion.span>
                <motion.svg
                  className="w-4 h-4 sm:w-5 sm:h-5"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                  animate={{
                    x: [0, 4, 0],
                    opacity: [1, 0.6, 1],
                  }}
                  transition={{
                    duration: 1.5,
                    repeat: Infinity,
                    ease: "easeInOut",
                  }}
                  aria-hidden="true"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M9 5l7 7-7 7"
                  />
                </motion.svg>
              </div>
            </motion.button>
          </motion.div>
        )}
      </AnimatePresence>

      <AnimatePresence>
        {isOpen && (
          <>
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="fixed inset-0 bg-black/50 z-50 backdrop-blur-sm"
              onClick={() => setIsOpen(false)}
              aria-hidden="true"
            />

            <motion.div
              initial={{ x: "-100%" }}
              animate={{ x: 0 }}
              exit={{ x: "-100%" }}
              transition={{ type: "spring", damping: 25, stiffness: 200 }}
              className="fixed left-0 top-0 bottom-0 w-80 md:w-96 bg-white z-50 shadow-2xl overflow-hidden flex flex-col"
              role="dialog"
              aria-modal="true"
              aria-labelledby="sidebar-title"
            >
              <div className="bg-gradient-to-r from-slate-800 to-slate-900 p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-orange-400 text-xs font-semibold uppercase tracking-wider mb-1">
                      {currentCategory}
                    </p>
                    <h3 id="sidebar-title" className="text-white text-lg font-bold">
                      Related Services
                    </h3>
                  </div>
                  <button
                    onClick={() => setIsOpen(false)}
                    className="w-10 h-10 rounded-full bg-white/10 hover:bg-white/20 flex items-center justify-center transition-colors"
                    aria-label="Close sidebar"
                  >
                    <svg
                      className="w-5 h-5 text-white"
                      fill="none"
                      viewBox="0 0 24 24"
                      stroke="currentColor"
                      aria-hidden="true"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M6 18L18 6M6 6l12 12"
                      />
                    </svg>
                  </button>
                </div>
              </div>

              <div className="flex-1 overflow-y-auto p-4 space-y-3">
                {categoryServices.map((service, index) => (
                  <motion.div
                    key={service.id}
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: index * 0.05 }}
                  >
                    <Link
                      href={`/services/${service.id}`}
                      onClick={() => setIsOpen(false)}
                      className="group flex items-center gap-4 p-3 rounded-xl hover:bg-orange-50 transition-all duration-200 border border-transparent hover:border-orange-200"
                    >
                      <div className="w-16 h-16 rounded-lg overflow-hidden flex-shrink-0 shadow-sm">
                        {typeof service.image === "string" ? (
                          <img
                            src={service.image}
                            alt={service.title}
                            className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-300"
                          />
                        ) : (
                          <Image
                            src={service.image}
                            alt={service.title}
                            width={64}
                            height={64}
                            className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-300"
                          />
                        )}
                      </div>
                      <div className="flex-1 min-w-0">
                        <h4 className="text-gray-900 font-semibold text-sm group-hover:text-orange-600 transition-colors line-clamp-2">
                          {service.title}
                        </h4>
                        <p className="text-gray-500 text-xs mt-1">
                          Click to explore
                        </p>
                      </div>
                      <svg
                        className="w-5 h-5 text-gray-400 group-hover:text-orange-500 group-hover:translate-x-1 transition-all flex-shrink-0"
                        fill="none"
                        viewBox="0 0 24 24"
                        stroke="currentColor"
                        aria-hidden="true"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M9 5l7 7-7 7"
                        />
                      </svg>
                    </Link>
                  </motion.div>
                ))}
              </div>

              <div className="p-4 border-t border-gray-100 bg-gray-50">
                <Link
                  href="/services"
                  onClick={() => setIsOpen(false)}
                  className="flex items-center justify-center gap-2 w-full py-3 bg-gradient-to-r from-orange-500 to-orange-600 text-white rounded-xl font-semibold text-sm hover:shadow-lg transition-all"
                >
                  View All Services
                  <svg
                    className="w-4 h-4"
                    fill="none"
                    viewBox="0 0 24 24"
                    stroke="currentColor"
                    aria-hidden="true"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M17 8l4 4m0 0l-4 4m4-4H3"
                    />
                  </svg>
                </Link>
              </div>
            </motion.div>
          </>
        )}
      </AnimatePresence>
    </>
  );
}

