    {
      url: `${siteUrl}/terms`,
      lastModified: new Date(),
      changeFrequency: "yearly",
      priority: 0.3,
    },
    {
      url: `${siteUrl}/cookie-policy`,
      lastModified: new Date(),
      changeFrequency: "yearly",
      priority: 0.3,
    },
  ];

  const productIds = [
    "lead-generation",
    "calling-followup",
    "crm-dashboards",
    "content-marketing",
    "internal-assistants",
  ];

  const productRoutes: MetadataRoute.Sitemap = productIds.map((id) => ({
    url: `${siteUrl}/products/${id}`,
    lastModified: new Date(),
    changeFrequency: "monthly" as const,
    priority: 0.7,
  }));

  const industryIds = [
    "startups-saas",
    "enterprises",
    "ecommerce",
    "real-estate",
    "healthcare",
    "education",
    "local-business",
  ];

  const industryRoutes: MetadataRoute.Sitemap = industryIds.map((id) => ({
    url: `${siteUrl}/industries/${id}`,
    lastModified: new Date(),
    changeFrequency: "monthly" as const,
    priority: 0.7,
  }));

  let blogRoutes: MetadataRoute.Sitemap = [];
  try {
    const res = await fetch(`${siteUrl}/api/home`, { cache: "no-store" });
    if (res.ok) {
      const data = await res.json();
      const posts = data.posts || [];
      blogRoutes = posts.map((post: { slug: string; publishedAt?: string }) => ({
        url: `${siteUrl}/blog/${post.slug}`,
        lastModified: post.publishedAt ? new Date(post.publishedAt) : new Date(),
        changeFrequency: "weekly" as const,
        priority: 0.6,
      }));
    }
  } catch {
  }

  return [...staticRoutes, ...productRoutes, ...industryRoutes, ...blogRoutes];
}

src/components/SafeBackgroundImage.tsx.content:
"use client";

import { useState, useEffect } from "react";

type SafeBackgroundImageProps = {
  src: string;
  className?: string;
  children?: React.ReactNode;
};

const placeholderGradient = "linear-gradient(135deg, #fef3c7, #ffedd5)";

export function SafeBackgroundImage({ src, className, children }: SafeBackgroundImageProps) {
  const [bgImage, setBgImage] = useState(`url(${src})`);
  const [hasError, setHasError] = useState(false);

  useEffect(() => {
    if (!src || hasError) return;

    const img = new Image();
    img.src = src;
    img.onerror = () => {
      setHasError(true);
      setBgImage(placeholderGradient);
    };
  }, [src, hasError]);

  return (
    <div
      className={className}
      style={{ backgroundImage: bgImage }}
    >
      {children}
    </div>
  );
}

