"use client";

import { useEffect, useMemo, useState } from "react";
import { useRouter } from "next/navigation";
import useSWR from "swr";
import Image from "next/image";
import Link from "next/link";
import { HeroSlide } from "@/components/HeroCarousel";
import { apiFetch } from "@/lib/api";
import { normalizeImageUrl } from "@/lib/images";

type About = {
  mission: string;
  vision: string;
  teamIntro: string;
  expertise: string[];
  coreValues: string[];
};

type TeamMember = {
  id: number;
  name: string;
  role: string;
  bio: string;
  expertise: string[];
  avatarUrl: string;
  isActive: boolean;
  displayOrder: number;
};

type BlogPost = {
  id: number;
  title: string;
  slug: string;
  excerpt: string;
  content: string;
  imageUrl: string;
  isPublished: boolean;
  publishedAt: string | null;
};

type JobPosting = {
  id: number;
  title: string;
  department: string;
  type: string;
  location: string;
  description: string;
  requirements: string[];
  isActive: boolean;
  displayOrder: number;
};

type SlideInput = Omit<HeroSlide, "id">;

const fetcher = (url: string) =>
  apiFetch(url, { credentials: "include" }).then((res) => {
    if (!res.ok) throw new Error("Unauthorized");
    return res.json();
  });

const parseJsonArray = (value: unknown): string[] => {
  if (Array.isArray(value)) return value;
  if (typeof value === "string") {
    try {
      const parsed = JSON.parse(value);
      return Array.isArray(parsed) ? parsed : [];
    } catch {
      return [];
    }
  }
  return [];
};

type NavItem = {
  id: string;
  label: string;
  icon: React.ReactNode;
};

const navItems: NavItem[] = [
  {
    id: "hero",
    label: "Hero Slides",
    icon: (
      <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
      </svg>
    ),
  },
  {
    id: "about",
    label: "About",
    icon: (
      <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
      </svg>
    ),
  },
  {
    id: "team",
    label: "Team Members",
    icon: (
      <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4.354a4 4 0 110 5.292M15 21H3v-1a6 6 0 0112 0v1zm0 0h6v-1a6 6 0 00-9-5.197m13.5-9a2.5 2.5 0 11-5 0 2.5 2.5 0 015 0z" />
      </svg>
    ),
  },
  {
    id: "blog",
    label: "Blog Posts",
    icon: (
      <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 20H5a2 2 0 01-2-2V6a2 2 0 012-2h10a2 2 0 012 2v1m2 13a2 2 0 01-2-2V7m2 13a2 2 0 002-2V9a2 2 0 00-2-2h-2m-4-3H9M7 16h6M7 8h6v4H7V8z" />
      </svg>
    ),
  },
  {
    id: "careers",
    label: "Careers",
    icon: (
      <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 13.255A23.931 23.931 0 0112 15c-3.183 0-6.22-.62-9-1.745M16 6V4a2 2 0 00-2-2h-4a2 2 0 00-2 2v2m4 6h.01M5 20h14a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
      </svg>
    ),
  },
];

export default function AdminPage() {
  const router = useRouter();
  const [activeSection, setActiveSection] = useState("hero");
  const [sidebarOpen, setSidebarOpen] = useState(true);

  const { data, mutate, error } = useSWR<{ slides: HeroSlide[] }>("/api/admin/hero-slides", fetcher);
  const { data: aboutData, mutate: mutateAbout } = useSWR<{ about: About | null }>("/api/admin/about", fetcher);
  const { data: teamData, mutate: mutateTeam } = useSWR<{ members: TeamMember[] }>("/api/admin/team-members", fetcher);
  const { data: blogData, mutate: mutateBlog } = useSWR<{ posts: BlogPost[] }>("/api/admin/blog-posts", fetcher);
  const { data: careersData, mutate: mutateCareers } = useSWR<{ jobs: JobPosting[] }>("/api/admin/careers", fetcher);

  const isAuthed = !error; // If there's no error from API calls, user is authenticated
  const [authError, setAuthError] = useState<string | null>(null);
  const [heroError, setHeroError] = useState<string | null>(null);
  const [heroUploading, setHeroUploading] = useState(false);

  const [form, setForm] = useState<SlideInput>({
    heading: "",
    subHeading: "",
    tags: [],
    backgroundImageUrl: "",
    isActive: true,
    displayOrder: 0,
