  const [editingId, setEditingId] = useState<number | null>(null);

  const [aboutForm, setAboutForm] = useState({
    mission: "",
    vision: "",
    teamIntro: "",
    expertise: [] as string[],
    coreValues: [] as string[],
  });
  const [aboutError, setAboutError] = useState<string | null>(null);
  const [aboutSuccess, setAboutSuccess] = useState<string | null>(null);

  const [memberForm, setMemberForm] = useState({
    name: "",
    role: "",
    bio: "",
    expertise: [] as string[],
    avatarUrl: "",
    isActive: true,
    displayOrder: 0,
  });
  const [editingMemberId, setEditingMemberId] = useState<number | null>(null);

  const [postForm, setPostForm] = useState({
    title: "",
    slug: "",
    excerpt: "",
    content: "",
    imageUrl: "",
    isPublished: false,
    publishedAt: "",
  });
  const [editingPostId, setEditingPostId] = useState<number | null>(null);
  const [teamError, setTeamError] = useState<string | null>(null);
  const [blogError, setBlogError] = useState<string | null>(null);
  const [blogUploading, setBlogUploading] = useState(false);
  const [memberUploading, setMemberUploading] = useState(false);

  const [jobForm, setJobForm] = useState({
    title: "",
    department: "",
    type: "Full-time",
    location: "Remote",
    description: "",
    requirements: [] as string[],
    isActive: true,
    displayOrder: 0,
  });
  const [editingJobId, setEditingJobId] = useState<number | null>(null);
  const [careersError, setCareersError] = useState<string | null>(null);

  useEffect(() => {
    if (data?.slides && editingId) {
      const slide = data.slides.find((s) => s.id === editingId);
      if (slide) {
        setForm({
          heading: slide.heading,
          subHeading: slide.subHeading,
          tags: slide.tags,
          backgroundImageUrl: slide.backgroundImageUrl,
          isActive: slide.isActive,
          displayOrder: slide.displayOrder,
        });
      }
    }
  }, [data?.slides, editingId]);

  useEffect(() => {
    if (aboutData?.about) {
      setAboutForm({
        mission: aboutData.about.mission,
        vision: aboutData.about.vision,
        teamIntro: aboutData.about.teamIntro ?? "",
        expertise: parseJsonArray(aboutData.about.expertise),
        coreValues: parseJsonArray(aboutData.about.coreValues),
      });
    }
  }, [aboutData?.about]);

  useEffect(() => {
    if (teamData?.members && editingMemberId) {
      const m = teamData.members.find((x) => x.id === editingMemberId);
      if (m) {
        setMemberForm({
          name: m.name,
          role: m.role,
          bio: m.bio,
          expertise: parseJsonArray(m.expertise),
          avatarUrl: m.avatarUrl,
          isActive: m.isActive,
          displayOrder: m.displayOrder,
        });
      }
    }
  }, [teamData?.members, editingMemberId]);

  useEffect(() => {
    if (blogData?.posts && editingPostId) {
      const p = blogData.posts.find((x) => x.id === editingPostId);
      if (p) {
        setPostForm({
          title: p.title,
          slug: p.slug,
          excerpt: p.excerpt,
          content: p.content,
          imageUrl: p.imageUrl,
          isPublished: p.isPublished,
          publishedAt: p.publishedAt ? new Date(p.publishedAt).toISOString().slice(0, 16) : "",
        });
      }
    }
  }, [blogData?.posts, editingPostId]);

  useEffect(() => {
    if (careersData?.jobs && editingJobId) {
      const j = careersData.jobs.find((x) => x.id === editingJobId);
      if (j) {
        setJobForm({
          title: j.title,
          department: j.department,
          type: j.type,
          location: j.location,
          description: j.description,
          requirements: parseJsonArray(j.requirements),
          isActive: j.isActive,
          displayOrder: j.displayOrder,
        });
      }
    }
  }, [careersData?.jobs, editingJobId]);

  const handleLogin = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setAuthError(null);
    const formData = new FormData(e.currentTarget);
    const payload = {
      email: formData.get("email") as string,
      password: formData.get("password") as string,
    };
    const res = await apiFetch("/api/admin/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
      credentials: "include",
    });
    if (!res.ok) {
      setAuthError("Invalid credentials");
      return;
    }
    try {
      const data = await res.json().catch(() => null);
      if (data?.token && typeof window !== "undefined") {
        localStorage.setItem("nano_admin_token", data.token);
      }
    } catch {}
    window.location.href = "/admin";
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setHeroError(null);
    const payload = {
      ...form,
      tags: form.tags.length ? form.tags : ["AI", "Automation"],
      displayOrder: Number(form.displayOrder),
    };
    const endpoint = editingId ? `/api/admin/hero-slides/${editingId}` : "/api/admin/hero-slides";
    const method = editingId ? "PUT" : "POST";

    const res = await apiFetch(endpoint, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
      credentials: "include",
    });

    if (res.ok) {
      setForm({ heading: "", subHeading: "", tags: [], backgroundImageUrl: "", isActive: true, displayOrder: 0 });
      setEditingId(null);
      await mutate();
    } else {
      const data = await res.json().catch(() => null);
      const issues = data?.issues?.map((i: { message?: string }) => i?.message).filter(Boolean).join(", ");
      setHeroError(issues || data?.error || "Could not save slide");
    }
  };

  const handleHeroFile = async (file: File) => {
    setHeroUploading(true);
    setHeroError(null);
    const formData = new FormData();
    formData.append("file", file);
    const res = await apiFetch("/api/uploads", { method: "POST", body: formData });
    setHeroUploading(false);
    if (!res.ok) {
      setHeroError("Image upload failed. Try a smaller file.");
      return;
    }
    const json = (await res.json()) as { url?: string };
    if (json.url) setForm((f) => ({ ...f, backgroundImageUrl: json.url! }));
  };

  const handleDelete = async (id: number) => {
    await apiFetch(`/api/admin/hero-slides/${id}`, { method: "DELETE", credentials: "include" });
    await mutate();
  };

  const handleAboutSave = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setAboutError(null);
    setAboutSuccess(null);
    const res = await apiFetch("/api/admin/about", {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      credentials: "include",
      body: JSON.stringify({
        mission: aboutForm.mission,
        vision: aboutForm.vision,
        teamIntro: aboutForm.teamIntro,
        expertise: aboutForm.expertise,
        coreValues: aboutForm.coreValues,
      }),
    });
    if (res.ok) {
      await mutateAbout();
      setAboutSuccess("About content saved.");
    } else {
      const data = await res.json().catch(() => null);
      setAboutError(data?.error || "Could not save About content");
    }
  };

  const handleMemberSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setTeamError(null);
    const payload = { ...memberForm, displayOrder: Number(memberForm.displayOrder) };
    const endpoint = editingMemberId ? `/api/admin/team-members/${editingMemberId}` : "/api/admin/team-members";
    const method = editingMemberId ? "PUT" : "POST";
    const res = await apiFetch(endpoint, {
      method,
      headers: { "Content-Type": "application/json" },
      credentials: "include",
      body: JSON.stringify(payload),
    });
    if (res.ok) {
      setMemberForm({ name: "", role: "", bio: "", expertise: [], avatarUrl: "", isActive: true, displayOrder: 0 });
      setEditingMemberId(null);
      await mutateTeam();
    } else {
      const data = await res.json().catch(() => null);
      setTeamError(data?.error || "Could not save member");
    }
  };

  const handleMemberDelete = async (id: number) => {
    await apiFetch(`/api/admin/team-members/${id}`, { method: "DELETE", credentials: "include" });
    await mutateTeam();
  };

  const handlePostSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setBlogError(null);
    const endpoint = editingPostId ? `/api/admin/blog-posts/${editingPostId}` : "/api/admin/blog-posts";
    const method = editingPostId ? "PUT" : "POST";
    const res = await apiFetch(endpoint, {
      method,
      headers: { "Content-Type": "application/json" },
      credentials: "include",
      body: JSON.stringify(postForm),
    });
    if (res.ok) {
      setPostForm({ title: "", slug: "", excerpt: "", content: "", imageUrl: "", isPublished: false, publishedAt: "" });
      setEditingPostId(null);
      await mutateBlog();
    } else {
      const data = await res.json().catch(() => null);
      setBlogError(data?.error || "Could not save post");
    }
  };

  const handlePostDelete = async (id: number) => {
    await apiFetch(`/api/admin/blog-posts/${id}`, { method: "DELETE", credentials: "include" });
    await mutateBlog();
  };

  const handleBlogImageUpload = async (file: File) => {
    setBlogUploading(true);
    setBlogError(null);
    const formData = new FormData();
    formData.append("file", file);
    const res = await apiFetch("/api/uploads", { method: "POST", body: formData });
    setBlogUploading(false);
    if (!res.ok) {
      setBlogError("Image upload failed.");
      return;
    }
    const json = (await res.json()) as { url?: string };
    if (json.url) setPostForm((f) => ({ ...f, imageUrl: json.url! }));
  };

  const handleMemberImageUpload = async (file: File) => {
    setMemberUploading(true);
    setTeamError(null);
    const formData = new FormData();
    formData.append("file", file);
    const res = await apiFetch("/api/uploads", { method: "POST", body: formData });
    setMemberUploading(false);
    if (!res.ok) {
      setTeamError("Image upload failed.");
      return;
    }
    const json = (await res.json()) as { url?: string };
    if (json.url) setMemberForm((f) => ({ ...f, avatarUrl: json.url! }));
  };

  const handleJobSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setCareersError(null);
    const endpoint = editingJobId ? `/api/admin/careers/${editingJobId}` : "/api/admin/careers";
    const method = editingJobId ? "PUT" : "POST";
    const res = await apiFetch(endpoint, {
      method,
      headers: { "Content-Type": "application/json" },
      credentials: "include",
      body: JSON.stringify(jobForm),
    });
    if (res.ok) {
      setJobForm({ title: "", department: "", type: "Full-time", location: "Remote", description: "", requirements: [], isActive: true, displayOrder: 0 });
      setEditingJobId(null);
      await mutateCareers();
    } else {
      const data = await res.json().catch(() => null);
      let errorMsg = data?.error || "Could not save job posting";
      if (data?.issues && Array.isArray(data.issues)) {
        const details = data.issues.map((i: { path?: string[]; message?: string }) => `${i.path?.join('.') || 'field'}: ${i.message}`).join(', ');
        errorMsg = `${errorMsg} - ${details}`;
      }
      setCareersError(errorMsg);
    }
  };

  const handleJobDelete = async (id: number) => {
    await apiFetch(`/api/admin/careers/${id}`, { method: "DELETE", credentials: "include" });
    await mutateCareers();
  };

  const sortedSlides = useMemo(
    () => (data?.slides ? [...data.slides].sort((a, b) => a.displayOrder - b.displayOrder) : []),
    [data?.slides]
  );

  useEffect(() => {
    if (!isAuthed) {
      router.replace("/login");
    }
  }, [isAuthed, router]);

  if (!isAuthed) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 flex items-center justify-center">
        <p className="text-sm text-gray-200">Redirecting to login‚Ä¶</p>
      </div>
