            <svg className={`w-5 h-5 transition-transform ${sidebarOpen ? "" : "rotate-180"}`} fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 19l-7-7 7-7m8 14l-7-7 7-7" />
            </svg>
            {sidebarOpen && <span className="text-sm">Collapse</span>}
          </button>
        </div>
      </aside>

      <div className="flex-1 flex flex-col">
        <header className="bg-white border-b border-gray-200 px-8 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-bold text-gray-900">
                {navItems.find((n) => n.id === activeSection)?.label}
              </h1>
              <p className="text-sm text-gray-500">Manage your website content</p>
            </div>
            <div className="flex items-center gap-4">
              <Link href="/" className="text-sm text-gray-600 hover:text-orange-600 transition">View Site</Link>
              <button
                onClick={async () => {
                  await apiFetch("/api/admin/logout", { method: "POST", credentials: "include" });
                  // Clear localStorage token
                  if (typeof window !== "undefined") {
                    localStorage.removeItem("nano_admin_token");
                  }
                  // Redirect to login page
                  router.push("/login");
                }}
                className="flex items-center gap-2 px-4 py-2 rounded-xl border border-gray-200 text-gray-700 hover:border-red-200 hover:text-red-600 transition"
              >
                <svg className="w-4 h-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
                </svg>
                Logout
              </button>
            </div>
          </div>
        </header>

        <main className="flex-1 p-8 overflow-y-auto">
          {activeSection === "hero" && (
            <div className="space-y-8">
              <div className="bg-white rounded-2xl shadow-sm border border-gray-100 p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-6">{editingId ? "Edit Slide" : "Create New Slide"}</h3>
                <form onSubmit={handleSubmit} className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Heading</label>
                      <input value={form.heading} onChange={(e) => setForm((f) => ({ ...f, heading: e.target.value }))} placeholder="Enter heading" className="w-full rounded-xl border border-gray-200 px-4 py-3 outline-none focus:border-orange-500 focus:ring-2 focus:ring-orange-500/20 transition" required />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Tags (comma separated)</label>
                      <input value={form.tags.join(", ")} onChange={(e) => setForm((f) => ({ ...f, tags: e.target.value.split(",").map((t) => t.trim()).filter(Boolean) }))} placeholder="AI, Automation" className="w-full rounded-xl border border-gray-200 px-4 py-3 outline-none focus:border-orange-500 focus:ring-2 focus:ring-orange-500/20 transition" />
                    </div>
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Sub Heading</label>
                    <textarea value={form.subHeading} onChange={(e) => setForm((f) => ({ ...f, subHeading: e.target.value }))} placeholder="Enter sub heading" rows={3} className="w-full rounded-xl border border-gray-200 px-4 py-3 outline-none focus:border-orange-500 focus:ring-2 focus:ring-orange-500/20 transition resize-none" required />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Background Image URL</label>
                    <input value={form.backgroundImageUrl} onChange={(e) => setForm((f) => ({ ...f, backgroundImageUrl: e.target.value }))} placeholder="https://..." className="w-full rounded-xl border border-gray-200 px-4 py-3 outline-none focus:border-orange-500 focus:ring-2 focus:ring-orange-500/20 transition" required />
                  </div>
                  <div className="flex items-center gap-4">
                    <label className="text-sm font-medium text-gray-700">Or upload image:</label>
                    <input type="file" accept="image/*" onChange={(e) => { const f = e.target.files?.[0]; if (f) handleHeroFile(f); }} className="text-sm" />
                    {heroUploading && <span className="text-sm text-orange-600">Uploading...</span>}
                  </div>
                  <div className="flex items-center gap-6">
                    <label className="flex items-center gap-2">
                      <input type="checkbox" checked={form.isActive} onChange={(e) => setForm((f) => ({ ...f, isActive: e.target.checked }))} className="w-4 h-4 rounded border-gray-300 text-orange-500 focus:ring-orange-500" />
                      <span className="text-sm text-gray-700">Active</span>
                    </label>
                    <div className="flex items-center gap-2">
                      <label className="text-sm text-gray-700">Order:</label>
                      <input type="number" value={form.displayOrder} onChange={(e) => setForm((f) => ({ ...f, displayOrder: Number(e.target.value) }))} className="w-20 rounded-lg border border-gray-200 px-3 py-2 text-center" />
                    </div>
                  </div>
                  {heroError && <p className="text-sm text-red-600">{heroError}</p>}
                  <div className="flex gap-3 pt-2">
                    <button type="submit" className="px-6 py-3 rounded-xl bg-gradient-to-r from-orange-500 to-amber-500 text-white font-semibold hover:-translate-y-0.5 transition shadow-lg shadow-orange-500/25">{editingId ? "Save Changes" : "Create Slide"}</button>
                    {editingId && <button type="button" onClick={() => { setEditingId(null); setForm({ heading: "", subHeading: "", tags: [], backgroundImageUrl: "", isActive: true, displayOrder: 0 }); }} className="px-6 py-3 rounded-xl border border-gray-200 text-gray-700 hover:bg-gray-50 transition">Cancel</button>}
                  </div>
                </form>
              </div>
              <div className="grid gap-4">
                {sortedSlides.map((slide) => (
                  <div key={slide.id} className="bg-white rounded-2xl shadow-sm border border-gray-100 p-6 flex flex-col md:flex-row md:items-center gap-6">
                    <div
                      className="w-32 h-20 rounded-xl bg-cover bg-center flex-shrink-0 border border-gray-200"
                      style={{ backgroundImage: `url(${normalizeImageUrl(slide.backgroundImageUrl)})` }}
                    />
                    <div className="flex-1">
                      <div className="flex items-center gap-3 mb-2">
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${slide.isActive ? "bg-green-100 text-green-700" : "bg-gray-100 text-gray-600"}`}>{slide.isActive ? "Active" : "Inactive"}</span>
                        <span className="text-xs text-gray-500">Order: {slide.displayOrder}</span>
