                    <div className="flex gap-2">
                      <button onClick={() => setEditingId(slide.id)} className="px-4 py-2 rounded-lg border border-gray-200 text-gray-700 hover:border-orange-300 hover:text-orange-600 transition text-sm">Edit</button>
                      <button onClick={() => handleDelete(slide.id)} className="px-4 py-2 rounded-lg border border-gray-200 text-gray-700 hover:border-red-200 hover:text-red-600 transition text-sm">Delete</button>
